				        }
				        
				        $PrintJobsBulk_obj = PrintJobsBulkTable::getInstance()->findOrCreateOneBy('id', null, $print_job_data);
				        $print_id = $PrintJobsBulk_obj->id;
				        
				        if($print_id){
				            $this->__StartPrintJobs();
