			$invoicesArr[0] = "9999999999999";
			$patientsArr[0] = "9999999999999";
			foreach($invoices as $kinv => $vinv)
			{
				$invoicesArr[] = $vinv['id'];
				$patientsArr[] = $vinv['ipid'];
