			foreach($finalInvoices as $kfit=>&$vfit)
			{				
				if($kfit == "open" || $kfit =="overdue" || $kfit == "all")
				{
					foreach ($vfit as &$row) {
						if (isset($all_warnings[$row['id']])) {
							$row[$kfit]['InvoiceWarnings'] = $all_warnings[$row['id']];
						}
					}
				}
			}
