				->where('e.ipid = p.ipid')
				->whereIn('p.ipid', $patientsArr)
				->andWhere('e.clientid = "' . $clientid . '"')
				->orderBy('e.epid_num ASC');
			$allPatientDetailsArr = $allPatDetails->fetchArray();

