				    $destination_path = PDFDOCX_PATH . '/' . $clientid . '/' . $batch_temp_folder . '/pdf_invoice_' . $post['unique_id'].'.pdf';
				    
				    $pdf->toFile($destination_path);
				    
				    return $destination_path;
				    
				    
				} else {
