					$_POST['invoice']['invoice_number'] = $high_invoice_nr['invoicenumber'];
				}

				//save here
				$edit_invoice = $hi_invoices_form->edit_invoice($_REQUEST['invoiceid'], $clientid, $_POST, $status);


				if(empty($_REQUEST['pdf']))
				{
					if($edit_invoice)
					{
