				/* -------------------------------------------- 1. Get patients ipids from ids ------------------------------------------------------- */
				$pat_ipid_ids = Doctrine_Query::create()
					->select('id, ipid')
					->from('PatientMaster')
					->whereIn('id', $patients);
				$patient_ipids_arr = $pat_ipid_ids->fetchArray();
				$patient_ipids[] = '99999999999999';
