							$admission_dates[$period_ipid] = $anlage6_arr[$period_ipid];
						}
					}

					$active_days[$period_ipid] = array_values(array_intersect($period_days_array, $active_days[$period_ipid]));
				}

// 				print_r($admission_dates); exit;


				if($_REQUEST['dbg'])
