				$hospital_ids[] = '999999999';
				foreach($hosparray as $hospital)
				{
					$hospital_ids[] = $hospital['id'];
					$location_types[$hospital['location_type']][] = $hospital['id'];
				}

				//get patient hospital locations if it has
				$patloc = Doctrine_Query::create()
					->select('*')
