				$selected_month_details['end'] = date('Y-m-d', strtotime($invoice_details['invoice_end']));

				$period_days_array = $patientmaster->getDaysInBetween($selected_month_details['start'], $selected_month_details['end'], "Y-m");

				if($_REQUEST['dbg'])
