				{
					if($bwinvoice_details['sapv_approve_date'] != '0000-00-00 00:00:00' && strlen($bwinvoice_details['sapv_approve_date']) > 0 && $bwinvoice_details['sapv_approve_date'] != '1970-01-01 00:00:00')
