				        $print_job_data['user'] = $userid;
				        $print_job_data['page'] =  APP_BASE . $this->getRequest()->getControllerName() . '/' . $this->getRequest()->getActionName();;
				        $print_job_data['output_type'] = 'pdf';
				        $print_job_data['status'] = 'active';
