			$bre_invoices = BreInvoices::get_period_patients_bre_invoices($ipids, $period);

			foreach($bre_invoices as $k_invoice => $v_invoice)
			{
				$invoice_ids[] = $v_invoice['ipid'];
			}
			$invoice_ids = array_unique($invoice_ids);

			return $invoice_ids;
		}

