			else if($total_patient_days <= '50')
			{
				$condition_shortcut['h'] = 'pc5';
			}
			else if($total_patient_days <= '60')
			{
				$condition_shortcut['h'] = 'pc6';
			}
			else if($total_patient_days <= '75')
			{
