			$bayern_invoices = new BayernInvoices();


			$decid = Pms_Uuid::decrypt($_REQUEST['id']);
			$ipid = Pms_CommonData::getIpid($decid);
			$clientid = $logininfo->clientid;
			$userid = $logininfo->userid;

			$pm = $patientmaster->getMasterData($decid, 0);
