				exit;
			}

			$pseudo_post['clientid'] = $clientid;
			$pseudo_post['shortcuts'] = $shortcuts['bayern'];
			$pseudo_post['master_data'] = $master_data;
			$pseudo_post['master_items'] = $master_items;
