			//removed rp_eb_3 from shortcuts arr because is allready calculated
			$shortcuts_array = array('rp_eb_1', 'rp_eb_2', 'rp_doc_1', 'rp_doc_2', 'rp_doc_3', 'rp_doc_4', 'rp_nur_1', 'rp_nur_2', 'rp_nur_3', 'rp_nur_4', 'rp_pat_dead');
