// sort $extra_data
            
            usort($extra_data['home_visit'], array(new Pms_Sorter('billable_date'), "_date_compare"));
//            $extra_data['home_visit'] = array_reverse($extra_data['home_visit']);

			if($this->getRequest()->isPost())
