			$table_html = $this->view->tabulate($invoice_user_printjobs,array("class"=>"datatable",'id'=>'print_jobs_table','escaped'=>false));
			$print_html .= $table_html;
			$print_html .= '</div>';
			
			
			$this->view->print_html = '';
