					$invoiceids_to_warn = implode(',', $_POST['document']);
				
					$this->forward('generatereminderinvoice', 'Invoicenew', null, array());
				
					return;
				}
				//ISPC-2609 + ISPC-2000 Ancuta 22.09.2020
				elseif($_POST['batch_print_more'])
