			        {
			            $html = preg_replace('/style=\"(.*)\"/i', '', $html);
			        }
		        }
			
		        // 				print_r($html); exit;
		        $pdf->setHTML($html);
		
		        //create public/joined_files/ dir
		        while(!is_dir(PDFJOIN_PATH))
