		public function get_sapvs_approved_details($ipids, $current_period = false)
		{
			$patientmaster = new PatientMaster();

			if(is_array($ipids))
			{
				$ipids_arr = $ipids;
			}
			else
			{
				$ipids_arr[] = $ipids;
			}

			if($current_period)
			{
