    				->where('status != "1"')
    				->andWhere(implode(" OR ", $sql_where))
    				->andWhere('verordnungam != "0000-00-00 00:00:00"')
    				->andWhere('verordnungbis != "0000-00-00 00:00:00"')
    				->andWhere('isdelete=0')
    				->orderBy('verordnungam ASC');
    			$droparray = $dropSapv->fetchArray();
