				{
					$sapvs_details[$v_sapv['id']]['approved_date'] = date('d.m.Y', strtotime($v_sapv['approved_date']));
				}
				else
				{
					$sapvs_details[$v_sapv['id']]['approved_date'] = "";
				}

				if(strlen(trim(rtrim($v_sapv['approved_number']))) > '0')
