								if(($shortcut[$ipid] == '37b6' || $shortcut[$ipid] == '37b5') && $master_data[$ipid][$v_period_day]['37b6']['qty'] < '2' && $v_day_course == 'U')
								{
									$master_data[$ipid][$v_period_day][$shortcut[$ipid]]['shortcut'][] = $shortcut[$ipid];
