					if(!in_array($v_period_day, $flatrate[$ipid]) && !in_array('37b2', $day_shortcuts[$ipid]) && in_array($v_period_day, $active_days_in_period[$ipid]) && !in_array($v_period_day, $hospital_days[$ipid]))
					{
