										$totals[$ipid][$shortcut[$ipid]] += $master_price_list[$ipid][$v_period_day][0][$day_location_type][$shortcut[$ipid]]['price'];
										$week_numbers_arr[$ipid][] = $week_number[$ipid];
										$last_koord_dates[$ipid][] = $v_period_day;
									}
									else if($shortcut[$ipid] == '37b7')
									{
