									{
										$shortcut[$ipid] = '37b5';
									}

									if(!in_array('37b2', $day_shortcuts[$ipid]) && !in_array('37b5', $day_shortcuts[$ipid]) && $shortcut[$ipid] != '37b5') //only b5 or b6
									{
										$shortcut[$ipid] = '37b6';
