											$shortcut[$ipid] = '37b7';
										}
									}

									if(!in_array('37b2', $day_shortcuts[$ipid]) && !in_array('37b7', $day_shortcuts[$ipid]) && $shortcut[$ipid] != '37b7')
									{
										$shortcut[$ipid] = '37b8';
										$week_number = date('W', strtotime($v_period_day));
									}

									if(!empty($shortcut[$ipid]))
