									(($classification_data[$v_cf_day]['beratung'] == '1' && $classification_data[$v_cf_day]['koordination'] == '0') || ($classification_data[$v_cf_day]['beratung'] == '1' && $classification_data[$v_cf_day]['koordination'] == '1')))
								{
