				{
				    $tokens['debtor_number'] = $master_data_all['patients'][$invoice_data['ipid']]['invoice_data']['debtor_number'];
				    $tokens['debitor_number'] = $master_data_all['patients'][$invoice_data['ipid']]['invoice_data']['debtor_number'];
				}
				else
