				    $tokens['full_invoice_number'] .= ' zur Rechnung '.$invoice_data['storned_invoice_number'];
				}
				
				if($params['stornopdf'] == '1' && $params['stornoid'] > '0')
				{
					$tokens['unique_id'] = $invoice_data['record_id'];
