 						$this->_redirect(APP_BASE . 'invoicenew/listremindersinvoicetemplates?flg=succ&mes='.urlencode($this->view->error_message));
 						exit;
 					}
 					else
 					{
 						$this->retain_values($post);
 					}
 					$this->resetuploadvars();
 				}
 		
 		
 		
