					    		}
					    	}
					    	else
					    	{
					       		$final_file = $this->generate_reminder_file($template_data[0], false, 'pdf', $batch_temp_folder, 'merge', $temp_files);
					    	}
					    }
					}
					
					//ISPC-2312 Ancuta 06.12.2020
