		        //what did you post?
		        $result['type'] = 'warning';
		        $result['message'] = $this->translate('No invoice selected for which to transmit HL7');
		        
		    } else {
		    
		        /*
		         * TODO
		         * check to see if user allowed to edit this invoice number
		         *
		         * $this->assertInvoiceAllowed();
		         */
		        
		        
