		private function retainValues($values, $prefix = '')
		{
			
			foreach($values as $key => $val)
			{
				//var_dump($val);
				if(!is_array($val))
				{
					$this->view->$key = $val;
					
				}
				else
				{
					//retain 1 level array used in multiple hospizvbulk form
					foreach($val as $k_val => $v_val)
					{
						if(!is_array($v_val))
						{
							$this->view->{$prefix . $key . $k_val} = $v_val; 
						}
					}
				}
			}
			//exit;
		}

