					if((date('Y-m-d', strtotime($v_pat_loc['valid_till'])) == $discharge_dead_date[$v_pat_loc['ipid']]) && empty($pat_locations_values[($k_pat_loc + 1)]['valid_till']) && $locations[$v_pat_loc['location_id']]['type'] == '1')
