				->andWhere('ipid LIKE "' . $ipid . '"')
				->andWhere('source_ipid = ""');
			$patient_course_res = $patient_course->fetchArray();

			$wrong_course_ids[] = '9999999999';
			if($patient_course_res)
			{
				foreach($patient_course_res as $k_course => $v_course)
