				{
					$usr = new User();

					$doctor = $usr->getUserDetails($kvnoactivity['doc_id'], false);
					$pflege = $usr->getUserDetails($kvnoactivity['pfl_id'], false);

					if(!empty($pflege[0]['first_name']) || !empty($pflege[0]['last_name']))
