					$file_name_real = basename($tmpstmp);

					$pdf->toFile(PDF_PATH . '/' . $tmpstmp . '/' . $pdfname . '.pdf');
					$_SESSION ['filename'] = $tmpstmp . '/' . $pdfname . '.pdf';

					/*
					$cmd = "zip -9 -r -P " . $logininfo->filepass . " uploads/" . $tmpstmp . ".zip " . "uploads/" . $tmpstmp . "; rm -r " . PDF_PATH . "/" . $tmpstmp;

					exec($cmd);
