			$this->view->jsmedcount = count($a_medic);
			$grid = new Pms_Grid($a_medic, 1, count($a_medic) + 99999, "listpatientbedarfmedicationadd.html");
			$this->view->bedarfmedicgrid = $grid->renderGrid();
			$this->view->rowcount = count($a_medic);
