				$post = $_POST;
				$post['medications_pdf'] = $this->view->medications_pdf;
				$post['bedarfsmedications_pdf'] = $this->view->bedarfsmedications_pdf;
				$this->generateformPdf(3, $post, 'Aufnahmeassessment', "recording_assessmentformpdf.html");
			}
		}

