				$grid_pdf = new Pms_Grid($medicarr, 1, count($medicarr), "listpatientmedicationedit_pdf.html");
				$grid_pdf->medication_master_array = $medication_master_array;
				$this->view->bedarfsmedications_pdf = $grid_pdf->renderGrid();
