			#######################################################
			//medis edit grid procedure end
			//medis edit grid procedure end
			//medis add grid procedure start
			$comma = ",";
			$userval = "'0'";

			foreach($pqarr as $key => $val)
			{
				$userval .= $comma . "'" . $val['id'] . "'";
				$comma = ",";
			}

			$MasterGroups = array("4", "5", "6");
			$usersgroups = $usergroup->getUserGroups($MasterGroups);

