			{
				$darm = '1';
			}
			else
			{
				$darm = '0';
			}

			if(in_array('2', $kunstliche))
			{
				$blase = '1';
			}
			else
			{
				$blase = '0';
			}

			if(in_array('3', $kunstliche))
			{
				$luftrohre = '1';
			}
			else
			{
				$luftrohre = '0';
