				$cntpers1 = $pcs[0]['cnt_last_name'] . ', ' . $pcs[0]['cnt_first_name'] . '; ' . $pcs[0]['cnt_street1'] . ', ' . $pcs[0]['cnt_zip'] . ' ' . $pcs[0]['cnt_city'] . ', ' . $pcs[0]['cnt_phone'] . ', ' . $status[$pcs[0]['cnt_familydegree_id']];
