					$month_days_arr[date('Y-m-d', strtotime($v_day))]['visits'] = array();
					if(empty($month_days_arr[date('Y-m-d', strtotime($v_day))]['shortcuts']))
					{
						$month_days_arr[date('Y-m-d', strtotime($v_day))]['shortcuts']['E'] = 0;
