					}
				}
				
				$saved_data = 1; 
			}

			
			$this->view->sys_months_array = $system_year_months_array;
			
			
			foreach($system_year_months_array as $m=>$nrd){
			    $start_month[$m] = $m.'-01';
			    $end_month[$m] = $m.'-'.$nrd;
