			$details['patientname'] = htmlspecialchars($parr['last_name']) . ", " . htmlspecialchars($parr['first_name']) . "\n" . htmlspecialchars($parr['street1']) . "&nbsp;" . htmlspecialchars($parr['zip']) . "\n" . htmlspecialchars($parr['city']);
