			$patient_admission_days = $patient_days[$ipid]['admission_days'];
			$hospiz_days_client_settings = $patient_days[$ipid]['hospiz']['real_days'];


			/* ----------------Patient - Healthinsurance & pflegestufe header data START -------------------------------------- */
